mapper.select:
SELECT RT_GRP_NO /* stUserRtGrp.getUserRtGrpInfo */ , (SELECT CD_NM FROM ST_STD_CD WHERE GRP_CD = 'UR005' AND CD = SYS_GB_CD) SYS_GB_NM , RT_GRP_NM , CONCAT(SUBSTRING(APLY_STR_DT,1,4), '-', SUBSTRING(APLY_STR_DT,5,2), '-', SUBSTRING(APLY_STR_DT,7,2)) AS APLY_STR_DT , CONCAT(SUBSTRING(APLY_END_DT,1,4), '-', SUBSTRING(APLY_END_DT,5,2), '-', SUBSTRING(APLY_END_DT,7,2)) AS APLY_END_DT FROM ST_RT_GRP_BASE WHERE USE_YN = 'Y'
AND APLY_STR_DT <= TO_CHAR(NOW(), 'YYYYMMDD') AND APLY_END_DT >= TO_CHAR(NOW(), 'YYYYMMDD')
AND SYS_GB_CD = #{sysGbCd}
AND RT_GRP_NM LIKE CONCAT('%', #{rtGrpNm}, '%')
ORDER BY CAST(RT_GRP_NO AS INTEGER) LIMIT #{rowsPerPage} OFFSET #{offset}
